FileIn.nextLine();
while(FileIn.hasNextLine()) {
    s = FileIn.nextLine().split(&quot; +&quot;);
    y = Integer.parseInt(s[2]);
    m = Integer.parseInt(s[3]);
    d = Integer.parseInt(s[4]);
    List.add(new Student(s[0] + &#39; &#39; + s[1], y, m, d, s[5]));
}
